/*
Sum all fields matching selector and return total
*/
function calculateSum(selector) {
	var sum = 0;
	jQuery(selector).each(function () {
		if (this.value.length != 0) {
			var total = this.value
			sum += ConvertToNumber(total);
		}
	});
	return sum;
}
